USE gongdan;
-- gongdan_user (用户信息表)
CREATE TABLE IF NOT EXISTS gongdan_user
(
    user_id          VARCHAR(255) COMMENT "用户ID",
    username         VARCHAR(255) COMMENT "用户名",
    nickname         VARCHAR(255) COMMENT "昵称",
    gender           VARCHAR(1) COMMENT "性别(M/F/U)",
    age              INT COMMENT "年龄",
    birthday         VARCHAR(10) COMMENT "生日 (YYYY-MM-DD)",
    register_date    VARCHAR(10) COMMENT "注册日期 (YYYY-MM-DD)",
    register_channel VARCHAR(255) COMMENT "注册渠道",
    vip_level        INT COMMENT "VIP 等级(O-普通用户)",
    vip_expire_date  VARCHAR(10) COMMENT "VIP 到期日期 (YYYY-MM-DD)",
    phone            VARCHAR(20) COMMENT "手机号",
    email            VARCHAR(255) COMMENT "邮箱",
    province         VARCHAR(100) COMMENT "省份",
    city             VARCHAR(100) COMMENT "城市",
    tags JSON COMMENT "用户标签", -- 使用JSON类型存储数组
    is_musician      TINYINT(1) COMMENT "是否音乐人 (0:否, 1:是)",
    musician_level   INT COMMENT "音乐人等级",
    update_time      DATETIME COMMENT "更新时间",
    PRIMARY KEY (user_id)         -- 假设user_id是主键
) COMMENT "用户信息表";

