-- gongdan_song (歌曲信息表)
CREATE TABLE IF NOT EXISTS gongdan_song
(
    song_id           VARCHAR(255) COMMENT "歌曲ID",
    song_name         VARCHAR(255) COMMENT "歌曲名称",
    song_alias        VARCHAR(255) COMMENT "歌曲别名",
    artist_id         VARCHAR(255) COMMENT "艺人ID",
    artist_name       VARCHAR(255) COMMENT "艺人名称",
    album_id          VARCHAR(255) COMMENT "专辑ID",
    album_name        VARCHAR(255) COMMENT "专辑名称",
    duration          INT COMMENT "时长(秒)",
    language          VARCHAR(100) COMMENT "语言",
    genre             VARCHAR(100) COMMENT "流派",
    publish_date      VARCHAR(10) COMMENT "发行日期 (YYYY-MM-DD)",
    copyright_company VARCHAR(255) COMMENT "版权公司",
    is_original       TINYINT(1) COMMENT "是否原创 (0:否, 1:是)",
    is_high_quality   TINYINT(1) COMMENT "是否高品质 (0:否, 1:是)",
    is_free           TINYINT(1) COMMENT "是否免费 (0:否, 1:是)",
    play_count_total  BIGINT COMMENT "总播放量",
    lyric_count       INT COMMENT "歌词数量",
    comment_count     INT COMMENT "评论数量",
    update_time       DATETIME COMMENT "更新时间",
    PRIMARY KEY (song_id) -- 假设song_id是主键
) COMMENT "歌曲信息表";

