    company          VARCHAR(255) COMMENT "发行公司",
    language         VARCHAR(100) COMMENT "语言",
    genre            VARCHAR(100) COMMENT "流派",
    song_count       INT COMMENT "歌曲数量",
    play_count_total BIGINT COMMENT "总播放量",
    description TEXT COMMENT "专辑描述", -- 使用TEXT类型
